import React, { useState } from "react";
import * as C from "./styles";

const Form = () => {
  const [desc, setDesc] = useState ("");
  const [amount, setAmount] = useState("");
  const [isExpense, setExpense] = useState("false");


  const handleSave = () => {
    if (!desc || !amount){
        alert("Informe a descrição e o  valor!");
            return;
    } else if (amount <1){
        alert("O valor tem que ser positivo!");
        return;
    }
};
    return(
       <>
       <C.Container>
        <C.InputContent>
            <C.label>Decrição </C.label>
            <C.input value={desc} onChange={(e) => setDesc (e.target.value)}/>
        </C.InputContent>
        <C.InputContent>
            <C.label>Valor</C.label>
            <C.input value={amount}  type="number" onChange={(e) => setAmount (e.target.value)}/>
        </C.InputContent>
        <C.RadioGroup>
            <C.input type="radio" id="rIncome" defaultChecked name="group1" onChange={(e) => setExpense (!isExpense)}/>
            <C.label htmlFor="rIncome">Entradas</C.label>
        <C.input type="radio" id="rExpenses" name="group1" onChange={(e) => setExpense (!isExpense)}
        />
        <C.label htmlFor="rExpenses">Saidas</C.label>
        </C.RadioGroup>
       </C.Container>
       </> 
       );
};

export default Form;
