import React, { useState } from "react";
import * as C from "./styles"

const Form = () => {
  const [desc, setDesc] = useState ("");
  const [amount, SetAmount] = useState("");
  const [isExpense, setExpense] = useState("false");


    return(
       <>
       <C.Container>
        <C.InputContent>
            <C.label>
                <C.input value={desc} onChange={(e) => setDesc (e.target.value)}/>
            </C.label>
        </C.InputContent>
        <C.InputContent>
            <C.label>Valor</C.label>
            <C.input value={amount}  type="number" onChange={(e) => setDesc (e.target.value)}/>
        </C.InputContent>
        <C.RadioGroup>
        <C.input type="number" id="rIncome" defaultChecked name="group1" onChange={(e) => setExpense (!isExpense)}
        />
        <C.label htmlFor="rIncome">Entrada</C.label>
        <C.input type="radio" id="rExpenses" name="group1" onChange={(e) => setExpense (!isExpense)}
        />
        <C.label htmlFor="rExpenses">Saidas</C.label>
        </C.RadioGroup>
       </C.Container>
       </> 
       );
};

export default Form;
